export class UdpServer {
    constructor({ address, port, sdk, retryTime, retryGap, failRetryGap, messageNoParse, retryWhenFail, }: {
        address: any;
        port: any;
        sdk: any;
        retryTime?: number;
        retryGap?: number;
        failRetryGap?: number;
        messageNoParse?: boolean;
        retryWhenFail?: boolean;
    });
    socket: WechatMiniprogram.UDPSocket;
    destroy: () => void;
    _onErrorHandler(errMsg: any): void;
    _onMessageHandler(resp: any): void;
    _cmdTypeHandler(message: any): void;
    _onProgressErrorHandler(error: any): void;
    onError(fn: any): UdpServer;
    _errorHandler: any;
    onProgressError(fn: any): UdpServer;
    _progressErrorHandler: any;
    onMessage(fn: any): UdpServer;
    _msgHandler: any;
    _send(message: any): void;
    send(msg: any, { validateResponse }?: {
        validateResponse: any;
    }): Promise<any>;
    aborted: boolean;
    abort(): void;
}
